    function isUserOrDappEligibleForPayment(address user_, address dapp_, bytes4 functionSig_, uint256 amount_) external view returns(bool);
    function updateEnterpriseEligibility(address user_, address dapp_, bytes4 functionSig_, uint256 amount_) external returns(bool);}","keccak256":"0x03a6641337503a16ba901afaf12b3f4dec85846baff7d4d1d143125da6d4e89b","license":"MIT"},"contracts/interface/dplat/IZbytePriceFeeder.sol":{"content":"// SPDX-License-Identifier: MIT

